def get_synonyms(language):
    default_category_keywords = {
        "sushi": ["—Å—É—à–∏", "—Ä–æ–ª", "maki", "–º–∞–∫—ñ"],
        "grill": ["–≥—Ä–∏–ª—å", "–±–∞—Ä–±–µ–∫—é", "–º—è—Å–æ", "—à–∞—à–ª—ã–∫", "—Å—Ç–µ–π–∫"],
