    }
    synonyms = STOPWORDS_DATA.get(language, {}).get("synonyms", {})
    return synonyms if synonyms else default_category_keywords

EXTRA_MENU_PHRASES = {
    "ru": ["–Ω–∞ –≤–µ—á–µ—Ä", "–Ω–∞ —É–∂–∏–Ω", "–≤–µ—á–µ—Ä–Ω–µ–µ –º–µ–Ω—é", "–≤–µ—á–µ—Ä–Ω–∏–µ –±–ª—é–¥–∞", "—Ä–µ–∫–æ–º–µ–Ω–¥—É–π –Ω–∞ –≤–µ—á–µ—Ä", "–∑–∞–∫–∞–∑ –Ω–∞ –≤–µ—á–µ—Ä"],
